# Define a custom strategy
from hypothesis import strategies as st
def custom_strategy():
    return st.data()

# Define a test function
@pytest.mark.unit
def test_my_route(myapp: FastAPI):
    def test_get_items(db: Fixture, myroute: str = Path('/items')):
        response = myapp.test_client().get(myroute)
        assert response.status_code == 200
