from typing import TypeVar, cast

T = TypeVar('T')

def warn_if_wrong_type(arg: T, expected_type: type) -> None:
    if not isinstance(arg, expected_type):
        warning.warn(f'Argument passed ({type(arg).__name__}) is not of type {expected_type.__name__}')
    cast(expected_type, arg)

