from typing import List, Optional

class WaveFunction:
    def __init__(self, coefficients: List[float]):
        self.coefficients = coefficients

def apply_wave_function(wave_function: WaveFunction, x: float) -> float:
    "Apply a wave function to a point in space."
    return sum([coefficient * math.sin(2 * math.pi * coefficient * x) for coefficient in wave_function.coefficients])
