from typing import TypedDict, List

class Sound(TypedDict):
  frequency: int
  duration: float

class CustomSound:
  sounds = {
      'beep': (3000, 0.5),
      'boop': (1500, 0.3),
  }

def play_sound(self, sound_name: str):
    frequency, duration = self.sounds[sound_name]
    winsound.Beep(frequency, int(duration * 1000))
