from typing import List, IO, Optional, Tuple

import zipfile

def read_files(zip_path: str, files: List[str]) -> List[Tuple[str, bytes]]:
    with zipfile.ZipFile(zip_path, 'r') as zf:
        results = []
        for file in files:
            entry = zf.getinfo(file)
            if entry is not None and entry.is_file():
                results.append((file, zf.read(entry))) 
    return results
