"import sys, typing
from typing import List, Type Var,
from zipimport ZipFile

MODULES: List[Type[module]] = []

def load_package(zipfile: ZipFile) -> None:
    for member in zipfile.infolist():
        if member.isdir() and member.filename == 'lib':
            package_path = member.filename
            break
    else:
        raise Exception('Package directory not found')

    for member in zipfile.infolist(package_path):
        if member.isdir():
            continue
        if member.filename.endswith('.py'):
            module = __import__(member.filename[:-3], fromlist=['']) 
            MODULES.append(module)

load_package(ZipFile("my_package.zip"))
