from typing import TypedDict, List, Optional
import zlib

class CompressedData(TypedDict):
    compressed: bytes
    original_size: int

def compress(data: bytes) -> CompressedData:
    if not data:
        return {'compressed': b'', 'original_size': 0}
    compressed = zlib.compress(data)
    original_size = len(data)
    return {'compressed': compressed, 'original_size': original_size}
