code_examples.code:
from typing import Optional, Union
import datetime as dt
from pytz import timezone

def get_localized_datetime(tz: str, optional_date: Union[dt.datetime, None] = None) -> Optional[dt.datetime]:
    if optional_date is None:
        current_datetime = dt.datetime.now(timezone(tz))
    else:
        current_datetime = optional_date.astimezone(timezone(tz))
    return current_datetime
