import pytest
@pytest.fixture
def test_input():
    return b'Test data for codecs_hk'

@pytest.mark.parametrize('encoding, expected', [("hkscs", "Test data for codecs_hk"), ("utf-8", "Test data for codecs_hk")])
def test_encode_decode(test_input, encoding, expected):
