def test_functools_lru_cache():
  @pytest.fixture
  def sample_function():
    # Your sample function here

  def tested_function(func=sample_function):
    return functools.lru_cache(maxsize=None)(func)

  tested_function()
  # Add test cases for the lru_cache decorator here

def test_functools_reduce():
  @pytest.fixture
  def sample_list():
    return [1, 2, 3, 4]

  def tested_function(func=lambda x: x[0]):
    return functools.reduce(func, sample_list)

  assert tested_function(sample_list) == 1
