code_examples.code:
from unittest.mock import Mock

@pytest.fixture
def mock_random():
    return Mock(
        random=
            Mock(
                choices=Mock(side_effect=[1, 2, 3])
            )
    )

def test_randomness_behavior():
    with mock_random():
        assert random.choice([1, 2, 3]) in [1, 2, 3]
