code_examples.code:
import pytest
from uuid import uuid4, uuid1, uuid5, uuid6,
        UUID,
        parse_uuid_string,
        uuid_to_bytes,
        bytes_to_uuid;

@pytest.mark.parametrize("version,(1, 4, 5, 6)", ["v1", "v4", "v5", "v6"])
def test_uuid_generation(version):
    if version == 1:
        uuid_instance = uuid1()
    elif version == 4:
        uuid_instance = uuid4()
    elif version == 5:
        uuid_instance = uuid5(0x2000, b'example')
    elif version == 6:
        uuid_instance = uuid6()
    assert isinstance(uuid_instance, UUID)

import pytest
from uuid import uuid4,
        parse_uuid_string,
        uuid_to_bytes;

@pytest.mark.parametrize("uuid, ("[a-f0-9]{8}-[a-f0-9]{4}-4[a-f0-9]{3}-[89ababcdefghij][a-f0-9]{3}-[a-f0-9]{12}","[a-zA-Z0-9_-]{36}")", ["valid_uuid", "invalid_uuid"])
def test_uuid_validation(uuid):
    try:
        parse_uuid_string(uuid)
        assert True
    except ValueError:
        assert False
