# Import required libraries
import fastapi
from fastapi.testclient import FastAPIClient
from hypothesis import given, strategies as st

app = fastapi.FastAPI()

@app.get('/example')
def example():
    return {'message': 'Hello World'} 

# Testing the /example endpoint
@given(st.text())
def test_example(client: FastAPIClient, text: str):
    response = client.get('/example')
    assert response.json()['message'] == f'Hello {text}' 
