from cmath import pi
from math import sin, cos

import pytest
from unittest.mock import patch

@pytest.fixture
def mock_sin():
    return patch('math.sin')

def test_cos(mock_sin):
    with mock_sin as mock:
        mock.return_value = 0.5
        assert round(cos(pi/2), 6) == round(1j, 6)
