from fastapi import FastAPI
app = FastAPI()

class Item(BaseModel):
    name: str
    description: str

@app.get('/items/{item_id}')
def get_item(item_id: int, response: Response):
    item_dummy = {'name': 'Foo', 'description': 'A very nice Item'}
    if item_id != 1:
        response.status_code = 404
        return {'detail': f'Item not found'}
    return item_dummy
