def pytest_configure(config):
    import compileall
    compileall.compile_dir("test_directory", hide_warnings=True)

@pytest.fixture(scope="module")
def compiled_files():
    import os
    for file in os.listdir("test_directory"):
        if file.endswith(".pyc"):
            yield open(file, "r")
