from fastapi import FastAPI, HTTP_400_BadRequest
app = FastAPI()
class ItemIn(BaseModel):
  name: str
  description: str
@app.post('/items/', response_model=ItemOut)
def create_item(item: ItemIn):
    if len(item.name) < 3:
        raise HTTP_400_BadRequest(content={'detail': 'Name too short'})
    return {'name': item.name, 'description': item.description}

from fastapi import Depends
from sqlalchemy.orm import Session
@app.dependency()
def get_db():
    db = get_database()
    return db
