code_examples.code:
from copy import deepcopy

def test_deep_copy():
    a = {'a': 1, 'b': 2}
    b = deepcopy(a)
    assert id(a) != id(b)
    assert a is not b
    a['c'] = 3
    assert a == {'a': 1, 'b': 2, 'c': 3}
    assert b == {'a': 1, 'b': 2}
