def setup_file(filename):
    open(filename, 'w').close() # Create an empty file
@pytest.fixture
def test_file():
    return test_file_name

@pytest.mark.parametrize('filename, content', [("test1.txt", "Line 1\nLine 2"), ("test2.txt", "Test data")])
def test_read(test_file):
    assert read_file(test_file) == ("Line 1\nLine 2") # For test1.txt
    assert read_file(test_file) == ("Test data") # For test2.txt
