code_examples.code:
import gzip
from unittest import TestCase, mock

class GzipTest(TestCase):
    @mock.patch('gzip.compress')
    def test_compression_with_mock(self, mock_compress):
        mock_compress.return_value = b'Compressed data'
        original_data = b'This is a test string'
        decompressed_data = gzip.decompress(mock_compress())
        assertEqual(original_data, decompressed_data)
