# Import necessary libraries
from fastapi import FastAPI, HTTPException
from pydantic import BaseModel
from hypothesis import given,
                             strategies as st

app = FastAPI()

class Item(BaseModel):
    name: str
    description: str
    price: float

@app.get('/items/{item_id}')
def get_item(item_id: int):
    if item_id not in [1, 2, 3]:
        raise HTTPException(status_code=404, detail='Item not found')
    return app.get_item_query(Item).filter(Item.item_id == item_id).first()

# Define Hypothesis strategies for the Item model
@given(st.integers())
def item_ids(draw):
    if draw < 4:
        return [1, 2, 3]
    else:
        return st.sampled_from(range(10))

# Define a test function for the get_item endpoint
@given(st.integers().filter(lambda n: n not in [1, 2, 3]))
def test_get_item_not_found(item_id):
    response = app.client().get(f'/items/{item_id}')
    assert response.status_code == 404
