    {
      "language": "Python",
      "code": "import fastapi\nfrom fastapi import FastAPI, Depends\nimport cProfile\nimport pstats\n\napp = FastAPI()\n\ndef train(data):\n    # Your ML training loop code here\n\n@app.post('/train')\ndef train_route(data: List[float]):\n    with cProfile.Profile() as pr:\n        train(data)\n    pr_stats = SortKey(pr, 'cumulative')\n    s = pstats.Stats(pr_stats)\n    s.strip_dirs().print_callgraph(20)\n    return {'status': 'success'}"
    }
