code_examples.code:
def test_nturl2path_parameterized():
    urls = [('http://example.com/?query=value'), ('http://example.com/foo%61/bar%62')]
    expected = ['http://example.com/?query=value', 'http://example.com/foo_a/bar_b']
    for url, expected_path in zip(urls, expected):
        with pytest.raises(ValueError):
            nturl2path(url) == expected_path

import mock
@mock.patch('urllib.parse.urlparse')
def test_nturl2path_with_mocked_urlparse():
    urlparse = mock.Mock()
    urlparse.return_value = MockURLParseResult(scheme='http', netloc='example.com', path='/foo%61/bar%62')
    assert nturl2path('http://example.com/foo%61/bar%62') == 'http://example.com/foo_a/bar_b'
