def test_plistlib_read(tmpdir):
    plist_path = tmpdir.join('test.plist').strpath
    with open(plist_path, 'w') as f:
        plistlib.dump({'key': 'value'}, f)
    result = plistlib.readPlist(plist_path)
    assert result == {'key': 'value'}

def test_plistlib_write(tmpdir):
    plist_data = {'key': 'value'}
    plist_path = tmpdir.join('test.plist').strpath
    with open(plist_path, 'w') as f:
        plistlib.writePlist(plist_data, f)
    result = plistlib.readPlist(plist_path) == plist_data
