def test_pprint_basic(pprint_fixture):
    data = {'key': 'value'}
    pprint_fixture.pprint(data)
    assert pprint_fixture.last_text == '{
  "key": "value"
}'

def test_pprint_custom_format(pprint_fixture):
    data = {'nested': [1, 2, 3]}
    pprint_fixture.register_format('NestedList', format_nested_list)
    pprint_fixture.pprint(data, width=20, depth=2, compact=True)
    assert pprint_fixture.last_text == '[1, 2]
'
