import pytest
from shlex import split as shlex_split

@pytest.fixture
def mock_shlex():
    def func(*args, **kwargs):
        return ['mock-arg1', 'mock-arg2']
    return func

def test_shlex_split(mock_shlex):
    assert shlex_split('command arg1 arg2') == mock_shlex()
