from fastapi import FastAPI, Depends
app = FastAPI()
@app.get('/trigger-task')
def trigger_task(scheduled_tasks):
scheduled_tasks.run_now()
return {'message': 'Task triggered!'}
# Implement scheduled tasks logic here

def test_trigger_task(app, mocker):
    scheduled_tasks = mocker.Mock()
    response = client.get('/trigger-task')
    assert response.json() == {'message': 'Task triggered!'}
