app = FastAPI()
nights_of_the_week = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday']
def nightly_task(app):
    for night in nights_of_the_week:
        if night == current_night:
            app.state.some_variable = 'updated' # Example of using FastAPI's state for storing task results

@app.on_event('startup')
def startup():
    schedule.every().day.at('00:00').do(nightly_task, app)
