code_examples.code:
from freezegun import freeze_time

@pytest.fixture
def freeze_time_fixture():
  with freeze_time('2023-01-01 12:00:00'):
    yield

from pytest_asyncio import async_fixture

@async_fixture
def async_freeze_time_fixture():
  yield from freeze_time('2023-01-01 12:00:00')

from pytest import mark, benchmark

@mark.benchmark
def test_performance():
  benchmark(some_function)
