def test_parse_toml(mocker):
    toml_data = {"key": "value"}
    mocker.patch("toml.load, return_value=toml_data)
    parsed_data = toml.load("test.toml")
    assert parsed_data == toml_data

def test_write_toml(tmp_path):
    toml_data = {"key": "value"}
    path = tmp_path / "test.toml"
    toml.dump(toml_data, path)
