code_examples.code:
def add(x, y):
    if not isinstance(x, (int, float)) or not isinstance(y, (int, float)):
        raise ValueError("Both arguments must be numbers.")
    return x + y

@pytest.mark.parametrize("args, expected", [([1, 2], 3), (["1", 2], ValueError)])
def test_add(args, expected):
    with pytest.raises(expected):
        add(*args)

def mock_external_lib():
    raise NotImplementedError("Unsupported operation.")

@pytest.fixture
def mock_library():
    return unittest.mock.Mock(side_effect=mock_external_lib)
