```python
import fastapi
from typer import Typer, command

app = fastapi.FastAPI()
cli = Typer(app)

@app.get('/')
def index():
    return {'message': 'Welcome to the CLI Tool!'}

@cli.command()
def hello():
    '''Prints a greeting message''' 
    print('Hello World!')

def main():
    app.include_router(cli)
    uvicorn.run(app, host='127.0.0.1', port=8000)
