from fastapi import FastAPI, Depends, Cache
app = FastAPI()
@app.get('/items/{item_id}')
async def read_item(item_id: str, db: sqlalchemy.orm.Session = Depends(get_db), cache: Cache = Depends(cache_backend)):
    item = db.query(Model).filter_by(id=item_id).first()
    if item and not cache.get(f'item_{item_id}'):
        cache.set(f'item_{item_id}', item)
    return cache.get(f'item_{item_id}')
