code_examples.code:
import cProfile
from io import StringIO
from fastapi import FastAPI
app = FastAPI()

def profile(func):
    pr = cProfile.Profile()
    def wrapper(*args, **kwargs):
        pr.enable()
        result = func(*args, **kwargs)
        pr.disable()
        pr.print_stats(sort=1)
        return result
    return wrapper

@app.get('/profile')
@profile
def test_profile():
    pass
if __name__ == '__main__':
    app.run()
