from fastapi import FastAPI
app = FastAPI()
@app.get('/')
def get():
    return {'message': 'Hello World'}

import hypothesis
hypothesis.register_profile(
    strategy='pytest', base_plugin=PytestBasePlugin()
)
@app.get('/{item}')
def get_item(item: str):
    return {'message': f'Item {item}'}
