from fastapi import FastAPI, Depends
import concurrent.futures

app = FastAPI()

job_queue = concurrent.futures.ThreadPoolExecutor(max_workers=5)

@app.post('/submit_job/')
def submit_job(job: Job):
    job_id = str(uuid.uuid4())
    job_future = job_queue.submit(process_job, job) 
    return {'job_id': job_id}

